import React, { useState } from "react";
import "./Sidebar.css";
import TwitterIcon from "@mui/icons-material/Twitter";
import SidebarOptions from "./SidebarOptions";
import HomeIcon from "@mui/icons-material/Home";
import SearchIcon from "@mui/icons-material/Search";
import ListAltIcon from "@mui/icons-material/ListAlt";
import MailOutlineIcon from "@mui/icons-material/MailOutline";
import NotificationsIcon from "@mui/icons-material/Notifications";
import BookmarkBorderIcon from "@mui/icons-material/BookmarkBorder";
import PermIdentityIcon from "@mui/icons-material/PermIdentity";
import MoreIcon from "@mui/icons-material/More";
import MoreHorizIcon from "@mui/icons-material/MoreHoriz";
import DoneIcon from "@mui/icons-material/Done";
import ListIcon from "@mui/icons-material/List";
import { Avatar, Button, IconButton, Menu, MenuItem } from "@mui/material";

const Sidebar = () => {
  const [anchorEl, setAnchorEl] = useState(null);
  const openMenu = Boolean(anchorEl);

  const handleClick = (e) => {
    setAnchorEl(e.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <div className="sidebar">
      <TwitterIcon className="sidebar_twitterIcon" />
      <SidebarOptions active Icon={HomeIcon} text="Home" />
      <SidebarOptions active Icon={SearchIcon} text="Explore" />
      <SidebarOptions active Icon={NotificationsIcon} text="Notifications" />
      <SidebarOptions active Icon={MailOutlineIcon} text="Messages" />
      <SidebarOptions active Icon={BookmarkBorderIcon} text="Bookmarks" />
      <SidebarOptions active Icon={ListAltIcon} text="Lists" />
      <SidebarOptions active Icon={PermIdentityIcon} text="Profile" />
      <SidebarOptions active Icon={MoreIcon} text="More" />

      <Button variant="outlined" className="sidebar-tweet">
        Tweet
      </Button>

      <div className="Profile_info">
        <Avatar src="https://cdn.pixabay.com/photo/2016/08/08/09/17/avatar-1577909_960_720.png" />
        <div className="user_info">
          <h4>Soumya Shruti</h4>
          <h5>@soumyru</h5>
        </div>
        <IconButton
          size="small"
          sx={{ ml: 2 }}
          aria-controls={openMenu ? "basic-menu" : undefined}
          aria-haspopup="true"
          aria-expanded={openMenu ? "true" : undefined}
          onClick={handleClick}
        >
          <MoreHorizIcon />
        </IconButton>
        <Menu
          id="basic-menu"
          anchorEl={anchorEl}
          open={openMenu}
          onClick={handleClose}
          onClose={handleClose}
        >
          <MenuItem className="Profile_info1">
            <Avatar src="https://cdn.pixabay.com/photo/2016/08/08/09/17/avatar-1577909_960_720.png" />
            <div className="user_info subUser_info">
              <div>
                <h4>Soumya Shruti</h4>
                <h5>@soumyru</h5>
              </div>
              <div>
                List
              </div>
            </div>
          </MenuItem>
        </Menu>
      </div>
    </div>
  );
};

export default Sidebar;
